import React from 'react';
import { Grid, Paper } from '@material-ui/core'
import Search from './Search'
import Table from './Table'
import ModeTable from './ModeTable'
import PRITable from './PRITable'
import RFTable from './RFTable'

let input
let selectedData

class ViprPit extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      elnot: "",
      mode: {
        ELNOT: "",
        PD: "",
        ScanType: "",
        IR: "",
        ScanPeriod: "",
        ModType: ""
      },
      showSelected: false,
      modeSelected: false
    }
  }

  handleSubmit = e => {
    input = this.state.elnot
    e.preventDefault();
    this.setState({elnot: ""})
  }

  handleChange = e => {
    this.setState({elnot: e.target.value});
  }

  keyPress = e => {
    if(e.keyCode === 13) 
    this.handleSubmit(e)
  }

  handleSelected = ({ selectedRowsData }) => {
    selectedData = selectedRowsData

    this.setState({
      showSelected: !!selectedData
    })
  }

  render() {
    return (
      <React.Fragment>
        <Grid container spacing={1}>
          <Grid container xs={12} sm={6} style={{height: "95vh"}}>
            <Grid item xs={12} >
              <Paper style={{padding: 10}}>
                <Search
                  handleSubmit={this.handleSubmit}
                  handleChange={this.handleChange}
                  keyPress={this.keyPress}
                  input={this.state.elnot}
                />
              </Paper>
            </Grid>
            <Grid item xs={12} >
              <Paper style={{marginTop: 10, padding: 10, height: "84vh"}}>
                <Table elnot={input} selected={this.handleSelected} />
              </Paper>
            </Grid>
          </Grid>
          <Grid container xs={12} sm={6} style={{height: "95vh"}} >
            <Grid item xs={12} >
              <Paper style={{padding: 10, height: "70vh"}}>
                {this.state.showSelected && <ModeTable selectedData={selectedData} />}
              </Paper>
            </Grid>
            <Grid item xs={6} >
              <Paper style={{padding: 10, height: "25vh"}}>
                <PRITable selectedData={selectedData} />
              </Paper>
            </Grid>
            <Grid item xs={6} >
              <Paper style={{padding: 10, height: "25vh"}}>
                <RFTable selectedData={selectedData} />
              </Paper>
            </Grid>
          </Grid>
        </Grid>
      </React.Fragment>
    );
  }
}

export default ViprPit;
