import React, { useState, useEffect } from 'react'
//import NewUsersForm from "./NewUserForm";
import PageHeader from "../../components/pages/PageHeader";
import PeopleOutlineTwoToneIcon from '@material-ui/icons/PeopleOutlineTwoTone';
import { Paper, makeStyles, TableBody, TableRow, Toolbar, InputAdornment } from '@material-ui/core';
import useTable from "../../components/useTable";
import Controls from "../../components/controls/Controls";
import { Search } from "@material-ui/icons";

import UsersService from '../../services/UsersService';

const useStyles = makeStyles(theme => ({
    pageContent: {
        margin: theme.spacing(5),
        padding: theme.spacing(3)
    },
    searchInput: {
        width: '75%'
    }
}))


const headCells = [
    { field: 'id', headerName: 'ID', width: 70 },
    { field: 'email', headerName: 'Email', width: 130 },
    { field: 'fullname', headerName: 'Name', width: 130 },
    { field: 'phonenumber', headerName: 'Phone Number', width: 150, disableSorting: true },
    { field: 'role', headerName: 'Role', width: 130 },
    { field: 'username', headerName: 'Username', width: 130 },
]

export default function TestTable3Page() {

    const classes = useStyles();
    const [records, setRecords] = useState([])
    const [filterFn, setFilterFn] = useState({ fn: items => { return items; } })
    
    console.log(records);

    useEffect(() => {
        UsersService.getUsers()
            .then(res => res.json())
            .then(data => setRecords(data));
    }, []);
    
    console.log(records);


    const {
        TblContainer,
    } = useTable(records);

    const handleSearch = e => {
        let target = e.target;
        setFilterFn({
            fn: items => {
                if (target.value === "")
                    return items;
                else
                    return items.filter(x => x.fullName.toLowerCase().includes(target.value))
            }
        })
    }

    return (
        <>
            <PageHeader
                title="New User"
                subTitle="Form design with validation"
                icon={<PeopleOutlineTwoToneIcon fontSize="large" />}
            />
            <Paper className={classes.pageContent}>
                {/* <EmployeeForm /> */}
                <Toolbar>
                    <Controls.Input
                        label="Search User"
                        className={classes.searchInput}
                        InputProps={{
                            startAdornment: (<InputAdornment position="start">
                                <Search />
                            </InputAdornment>)
                        }}
                        onChange={handleSearch}
                    />
                </Toolbar>
                <TblContainer>
                    <TableBody>
                        {
                            records.map(item =>
                                (<TableRow key={item.id}>
                                    <TableRow>{item.id}</TableRow>
                                    <TableRow>{item.email}</TableRow>
                                    <TableRow>{item.fullname}</TableRow>
                                    <TableRow>{item.phonenumber}</TableRow>
                                    <TableRow>{item.role}</TableRow>
                                    <TableRow>{item.username}</TableRow>
                                </TableRow>)
                            )
                        }
                    </TableBody>
                </TblContainer>
            </Paper>
        </>
    )
}
