import React, { useState, useEffect } from 'react'
import PageHeader from "../home/PageHeader";
import PeopleOutlineTwoToneIcon from '@material-ui/icons/PeopleOutlineTwoTone';
import { Paper, makeStyles, TableBody, TableRow, Toolbar, InputAdornment, TableCell } from '@material-ui/core';
import useTable from "../../controls/useTable";
import Controls from "../../controls/Controls";
import Popup from "../../controls/Popup";
import { Search } from "@material-ui/icons";
import AddIcon from '@material-ui/icons/Add';
import EditOutlinedIcon from '@material-ui/icons/EditOutlined';
import CloseIcon from '@material-ui/icons/Close';

import InterceptsService from '../../../services/InterceptsService';

const useStyles = makeStyles(theme => ({
    pageContent: {
        margin: theme.spacing(5),
        padding: theme.spacing(3)
    },
    searchInput: {
        width: '75%'
    },
    newButton: {
        position: 'absolute',
        right: '10px'
    }
}))


const headCells = [
    { id: 'elnot', label: 'Elnot', width: 70 },
    { id: 'rf1', label: 'RF1', width: 70 },
    { id: 'rf2', label: 'RF2', width: 130 },
    { id: 'rf3', label: 'RF3', width: 150 },
    { id: 'pri1', label: 'PRI1', width: 130 },
    { id: 'pri2', label: 'PRI2', width: 130 },
    { id: 'pri3', label: 'PRI3', width: 130},
    { id: 'pd', label: 'PD', width: 130 },
    { id: 'scan_type', label: 'Scan Type', width: 130 },
    { id: 'ir', label: 'IR', width: 150 },
    { id: 'scan_period', label: 'Scan Period', width: 130 },
    { id: 'mod_type', label: 'Mod Type', width: 130 },
    { id: 'actions', label: 'Actions'}
]

export default function ViprPitComponent() {

    const classes = useStyles();
    const [recordForEdit, setRecordForEdit] = useState(null)
    const [records, setRecords] = useState([])
    const [filterFn, setFilterFn] = useState({ fn: items => { return items; } })
    const [openPopup, setOpenPopup] = useState(false)

    useEffect(() => {
        InterceptsService.getIntercepts()
        .then(response => {
            setRecords(response.data);
        })
    }, []);

    console.log(records);

    const {
        TblContainer,
        TblHead,
        TblPagination,
        recordsAfterPagingAndSorting
    } = useTable(records, headCells, filterFn);

    const handleSearch = e => {
        let target = e.target;
        setFilterFn({
            fn: items => {
                if (target.value === "")
                    return items;
                else
                    return items.filter(x => x.elnot.toLowerCase().includes(target.value))
            }
        })
    }
    
    const addOrEdit = (intercepts, resetForm) => {
        if (intercepts.elnot === 0)
        InterceptsService.addIntercepts(intercepts)
        else
        InterceptsService.updateIntercepts(intercepts)
        resetForm()
        setRecordForEdit(null)
        setOpenPopup(false)
        setRecords(InterceptsService.getIntercepts())
    }

    const openInPopup = item => {
        setRecordForEdit(item)
        setOpenPopup(true)
    }
    
    return (
        <>
            <PageHeader
                title="Intercepts"
                subTitle="Test Subtitle"
                icon={<PeopleOutlineTwoToneIcon fontSize="large" />}
            />
            <Paper className={classes.pageContent}>
                <Toolbar>
                    <Controls.Input
                        label="Search Users"
                        className={classes.searchInput}
                        InputProps={{
                            startAdornment: (<InputAdornment position="start">
                                <Search />
                            </InputAdornment>)
                        }}
                        onChange={handleSearch}
                    />
                    <Controls.Button
                        text="Add New"
                        variant="outlined"
                        startIcon={<AddIcon />}
                        className={classes.newButton}
                        onClick={() => { setOpenPopup(true); setRecordForEdit(null); }}
                    />
                </Toolbar>
                <TblContainer>
                    <TblHead />
                    <TableBody>
                        {
                            recordsAfterPagingAndSorting(records).map(item =>
                                (<TableRow key={item.elnot}>
                                    <TableCell>{item.elnot}</TableCell>
                                    <TableCell>{item.rf1}</TableCell>
                                    <TableCell>{item.rf2}</TableCell>
                                    <TableCell>{item.rf3}</TableCell>
                                    <TableCell>{item.pri1}</TableCell>
                                    <TableCell>{item.pri2}</TableCell>
                                    <TableCell>{item.pri3}</TableCell>
                                    <TableCell>{item.pd}</TableCell>
                                    <TableCell>{item.scan_type}</TableCell>
                                    <TableCell>{item.ir}</TableCell>
                                    <TableCell>{item.scan_period}</TableCell>
                                    <TableCell>{item.mod_type}</TableCell>
                                    <TableCell>
                                        <Controls.ActionButton
                                            color="primary"
                                            onClick={() => { openInPopup(item) }}>
                                            <EditOutlinedIcon fontSize="small" />
                                        </Controls.ActionButton>
                                        <Controls.ActionButton
                                            color="secondary">
                                            <CloseIcon fontSize="small" />
                                        </Controls.ActionButton>
                                    </TableCell>
                                </TableRow>)
                            )
                        }
                    </TableBody>
                </TblContainer>
                <TblPagination />
            </Paper>            
            <Popup
                title="User Form"
                openPopup={openPopup}
                setOpenPopup={setOpenPopup}
            >
            </Popup>
        </>
    )
}
