import React, { useState, useEffect } from 'react'
import MaterialTable from 'material-table';
import { Grid, Paper, makeStyles } from '@material-ui/core';

import ViprTablesViewService from '../../../services/ViprTablesViewService';


const useStyles = makeStyles(theme => ({
  elnotTbl: {
      margin: theme.spacing(5),
      padding: theme.spacing(3),
      width: '25%'
  },
  searchInput: {
      width: '75%'
  },
  newButton: {
      position: 'absolute',
      right: '10px'
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  tableRow: {
      "&.Mui-selected, &.Mui-selected:hover": {
              backgroundColor: "red",
              "& > .MuiTableCell-root": {
              color: "black"
          }
      }
  },
}))

export default function MaterialTableDemo() {

  const classes = useStyles();
  const [columns, setColumns] = useState([]);
  const [vipr, setvipr] = useState({ data: [] });

  useEffect(() => {
    setColumns([      
      { field: 'elnot', title: 'Elnot', width: 50 },
    ])

    ViprTablesViewService.getViprTablesView()
    .then(response => {
      setvipr({data: response.data });
    })
  }, []);

  console.log(vipr.data);

  return (
    <Grid container spacing={3} direction="column">
      <Grid container xs={12} spacing={1}>
        <Grid item xs={4}>
          <Paper className={classes.paper}>
            <MaterialTable
              title="Vipr-Pit"
              columns={columns}
              data={vipr.data}
              editable={{
                onRowAdd: newData =>
                  new Promise(resolve => {
                    setTimeout(() => {
                      resolve();
                      const data = [...vipr.data];
                      data.push(newData);
                      setvipr({ ...vipr, data });
                    }, 600);
                  }),
                onRowUpdate: (newData, oldData) =>
                  new Promise(resolve => {
                    setTimeout(() => {
                      resolve();
                      const data = [...vipr.data];
                      data[data.indexOf(oldData)] = newData;
                      setvipr({ ...vipr, data });
                    }, 600);
                  }),
              }}
            />
          </Paper>        
        </Grid>
      </Grid>    
    </Grid>      
  );
}
